INSERT INTO `binhluans` VALUES (1,1,1,'tuyệt vời trải nghiệm khó quên mong rằng mõi người đặt tour thật nhiều để web phát triển',5,1,1,'2020-12-19 13:09:39','2020-12-20 14:33:47'),(2,1,8,'Thật tuyệt vời thiên nhiên ở đây thật hùng vĩ, nó đẹp hơn rất nhiều so với tôi tưởng tượng !',5,1,1,'2020-12-20 02:08:26','2020-12-20 02:09:51'),(3,1,9,'Một trải nghiệm khá thú vị về nơi này, hi vọng rằng lần đi tiếp theo sẽ có nhiều thứ hấp dẫn hơn.',4,1,1,'2020-12-20 02:11:59','2020-12-23 07:39:55'),(4,2,8,'Con về quê Bác - làng Sen\nNgỡ như gặp lại thân quen làng mình\nVẫn hàng tre, vẫn mái đình\nĐỏ hoa râm bụt, thắm tình nước non\n\nĐơn sơ mái lá hãy còn\nTuổi thơ Người chạy lon ton chỗ nào?\nXóm làng, đồng ruộng, bờ ao\nCúi hôn nắm đất nghẹn ngào nhớ thương\n\nBác ơi! Năm tháng dặm trường\nThiêng liêng hai tiếng quê hương nghẹn lòng\nTrước khi từ giã cõi trần\nCâu hò xứ Nghệ gọi thầm trong tim\n\nLàng Sen chan chứa nỗi niềm\nYêu thương ôm cả mọi miền nhân gian.',5,0,1,'2020-12-24 01:33:42','2021-01-09 01:24:49'),(5,5,8,'',5,NULL,1,'2020-12-24 07:19:06','2020-12-24 07:19:06'),(6,4,8,'',4,NULL,1,'2020-12-24 07:20:05','2020-12-24 07:22:35'),(7,6,8,'',5,NULL,1,'2020-12-24 10:20:37','2020-12-24 10:20:37'),(8,11,8,'',3,NULL,1,'2020-12-24 10:22:28','2020-12-24 10:22:28'),(12,9,1,'không hài lòng lắm',2,0,1,'2021-01-08 07:30:36','2021-05-11 09:03:57'),(13,5,1,'',1,NULL,1,'2021-01-15 03:01:30','2021-05-24 08:15:59'),(14,8,1,'không ổn lắm',3,NULL,1,'2021-01-15 14:11:35','2021-05-22 02:54:26'),(15,7,1,'tour rất đẹp',5,NULL,1,'2021-01-22 03:01:51','2021-05-11 08:59:46'),(16,4,1,'Tuyệt vời đây là kỳ nghỉ khó quên',5,NULL,1,'2021-01-22 08:52:48','2021-01-22 08:52:48'),(17,19,1,'tour có nhiều cảnh đẹp',4,NULL,1,'2021-05-30 00:56:03','2021-05-30 00:56:03');
