INSERT INTO `camnangduliches` VALUES (4,'Mùa du lịch','fab fa-pagelines','Những thời điểm tốt nhất dành cho du lịch của từng địa điểm mà bạn muốn đến.','2020-12-20 00:51:16','2020-12-20 01:43:30',1),(5,'Địa điểm đẹp','fas fa-map-marker-alt','Các điểm đến, danh lam thắng cảnh của những thành phố bạn sẽ tới tham quan trong chuyến đi.','2020-12-20 00:54:01','2020-12-20 00:54:01',1),(6,'Chi phí','fas fa-dollar-sign','Thông tin những khoản chi phí phát sinh mà bạn cần dự phòng chuyến đi của mình.','2020-12-20 00:54:36','2020-12-20 01:06:32',1),(7,'Phương tiện di chuyển','fas fa-car','Thông tin các phương tiện bạn có thể sử dụng để thăm các địa điểm đẹp trong chuyến đi.','2020-12-20 00:55:16','2020-12-20 00:55:16',1),(8,'Du lịch khám phá','fab fa-instagram','Quý khách sẽ được khám phá những danh lam thắng cảnh được yêu thích nhất trên thế giới','2020-12-20 01:44:59','2020-12-28 01:39:36',0);
